import{_ as e,c as l,o as a,a as o}from"./app.d0b8b33a.js";const f=JSON.parse('{"title":"Unreal \u667A\u80FD\u6307\u9488","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u57FA\u672C\u6A21\u677F\u7C7B","slug":"\u57FA\u672C\u6A21\u677F\u7C7B"},{"level":2,"title":"\u5F15\u7528\u8BA1\u6570\u63A7\u5236\u5668","slug":"\u5F15\u7528\u8BA1\u6570\u63A7\u5236\u5668"},{"level":2,"title":"\u5DE5\u5177\u51FD\u6570","slug":"\u5DE5\u5177\u51FD\u6570"}],"relativePath":"basic/pointer.md","lastUpdated":1660019397000}'),r={name:"basic/pointer.md"},d=o('<h1 id="unreal-\u667A\u80FD\u6307\u9488" tabindex="-1">Unreal \u667A\u80FD\u6307\u9488 <a class="header-anchor" href="#unreal-\u667A\u80FD\u6307\u9488" aria-hidden="true">#</a></h1><p><a href="https://docs.unrealengine.com/5.0/zh-CN/smart-pointers-in-unreal-engine/" target="_blank" rel="noopener noreferrer">\u5B98\u65B9\u6587\u6863</a></p><p>Unreal \u6CA1\u6709\u4F7F\u7528 C++ \u6807\u51C6\u5E93\u4E2D\u7684\u667A\u80FD\u6307\u9488\uFF0C\u800C\u662F\u81EA\u5B9A\u4E49\u4E86\u4E00\u5957\u667A\u80FD\u6307\u9488\u5E93\u3002\u76F8\u8F83\u4E8E\u6807\u51C6\u5E93\u5B9E\u73B0\uFF0CUnreal \u667A\u80FD\u6307\u9488\u5E93\u6709\u5982\u4E0B\u7279\u70B9\uFF1A</p><ul><li>\u8BED\u6CD5\u7B80\u6D01\uFF1A\u4F7F\u7528\u65B9\u6CD5\u4E0E\u6807\u51C6\u5E93\u7C7B\u4F3C\uFF0C\u5E76\u4E14\u5B9E\u73B0\u4E86\u667A\u80FD\u5F15\u7528\u3002</li><li>\u53EF\u9009\u7684\u7EBF\u7A0B\u5B89\u5168\uFF1A\u63D0\u4F9B\u7EBF\u7A0B\u5B89\u5168\u5B9E\u73B0\u4F9B\u5F00\u53D1\u8005\u9009\u62E9\uFF0C\u5173\u95ED\u53EF\u83B7\u5F97\u66F4\u5FEB\u7684\u901F\u5EA6\u3002</li><li>\u5168\u5E73\u53F0\u652F\u6301\uFF1AC++ \u6807\u51C6\u5E93\u4E0D\u80FD\u63D0\u4F9B\u5168\u6E38\u620F\u5E73\u53F0\u7684\u652F\u6301\u3002</li><li>\u4F4E\u5185\u5B58\u5360\u7528\uFF1A64-bit \u7CFB\u7EDF\u4E0B\u5185\u5B58\u5360\u7528\u4EC5\u4E3A 16 \u5B57\u8282\uFF0C\u4E24\u500D\u539F\u751F\u6307\u9488\u5927\u5C0F\u3002</li><li>\u65E0\u5916\u90E8\u4F9D\u8D56\uFF1A\u5B8C\u5168\u7531 Unreal \u6838\u5FC3\u5E93\u548C C++ \u539F\u751F\u8BED\u6CD5\u5B9E\u73B0\u3002</li></ul><h2 id="\u57FA\u672C\u6A21\u677F\u7C7B" tabindex="-1">\u57FA\u672C\u6A21\u677F\u7C7B <a class="header-anchor" href="#\u57FA\u672C\u6A21\u677F\u7C7B" aria-hidden="true">#</a></h2><p><code>TSharedRef&lt;&gt;</code></p><ul><li>\u667A\u80FD\u5F15\u7528\uFF0C\u62E5\u6709\u4E0E C++ \u5F15\u7528\u76F8\u4F3C\u7684\u7279\u6027\uFF0C\u4F46\u5B9E\u73B0\u65B9\u5F0F\u4ECD\u4E3A\u5BF9\u539F\u751F\u6307\u9488\u7684\u62D3\u5C55</li><li>\u975E\u7A7A\uFF0C\u6784\u9020\u65F6\u9700\u4F20\u5165\u6709\u6548\u5BF9\u8C61</li><li>\u53C2\u4E0E\u5F15\u7528\u8BA1\u6570\uFF0C\u62E5\u6709\u6240\u6709\u6743</li></ul><p><code>TSharedPtr&lt;&gt;</code></p><ul><li>\u667A\u80FD\u6307\u9488\uFF0C\u62E5\u6709\u4E0E C++ \u6307\u9488\u76F8\u4F3C\u7684\u7279\u6027\uFF0C\u5B9E\u73B0\u65B9\u5F0F\u4E3A\u5BF9\u539F\u751F\u6307\u9488\u7684\u62D3\u5C55\u3002</li><li>\u53C2\u4E0E\u5F15\u7528\u8BA1\u6570\uFF0C\u62E5\u6709\u6240\u6709\u6743</li></ul><p><code>TWeakPtr&lt;&gt;</code></p><ul><li>\u5F31\u6307\u9488\uFF0C\u62E5\u6709\u4E0E C++ \u6307\u9488\u76F8\u4F3C\u7684\u7279\u6027\uFF0C\u5B9E\u73B0\u65B9\u5F0F\u4E3A\u5BF9\u539F\u751F\u6307\u9488\u7684\u62D3\u5C55\u3002</li><li>\u4E0D\u53C2\u4E0E\u5F15\u7528\u8BA1\u6570\uFF0C\u4E0D\u62E5\u6709\u6240\u6709\u6743</li></ul><p>\u4ECE\u6027\u80FD\u548C\u5B89\u5168\u6027\u7684\u89D2\u5EA6\u8003\u8651\uFF0C\u5728\u53EF\u4F7F\u7528\u667A\u80FD\u5F15\u7528\u7684\u573A\u666F\u4E0B\uFF0C\u5E94\u4F18\u5148\u9009\u62E9\u4F7F\u7528\u667A\u80FD\u5F15\u7528\u3002</p><h2 id="\u5F15\u7528\u8BA1\u6570\u63A7\u5236\u5668" tabindex="-1">\u5F15\u7528\u8BA1\u6570\u63A7\u5236\u5668 <a class="header-anchor" href="#\u5F15\u7528\u8BA1\u6570\u63A7\u5236\u5668" aria-hidden="true">#</a></h2><p>Unreal \u667A\u80FD\u6307\u9488\u7684\u5F15\u7528\u8BA1\u6570\u8868\u7531 <code>ReferenceController</code> \u7EF4\u62A4\uFF0C\u5927\u5C0F\u4EC5 16 \u5B57\u8282\u3002\u6BCF\u4E2A\u63A7\u5236\u5668\u62E5\u6709 <code>SharedReferenceCount</code> \u548C <code>WeakReferenceCount</code> \u4E24\u4E2A\u8BA1\u6570\u5668\uFF0C\u5206\u522B\u8BB0\u5F55\u667A\u80FD\u6307\u9488\uFF08\u6216\u5F15\u7528\uFF09\u4E0E\u5F31\u6307\u9488\u7684\u6570\u91CF\u3002</p><p>\u4E00\u4E2A\u667A\u80FD\u6307\u9488\u63A7\u5236\u5668\u7684\u751F\u547D\u5468\u671F\u5982\u4E0B\uFF1A</p><ul><li>\u5B9E\u4F8B\u5316\uFF1A\u5728 <code>ReferenceController</code> \u5BF9\u8C61\u88AB\u6784\u9020\u65F6\uFF0C\u4E24\u4E2A\u8BA1\u6570\u5668\u7684\u503C\u5747\u521D\u59CB\u5316\u4E3A <code>1</code></li><li>\u589E\u52A0\u8BA1\u6570\uFF1A <ul><li>\u5728\u667A\u80FD\u6307\u9488\uFF08\u6216\u5F15\u7528\uFF09\u7684\u62F7\u8D1D\u6784\u9020\u51FD\u6570\u4E2D\u589E\u52A0\u8BA1\u6570\u5668 <code>SharedReferenceCount</code> \u8BA1\u6570</li><li>\u5728\u5F31\u6307\u9488\u7684\u62F7\u8D1D\u6784\u9020\u51FD\u6570\u4E2D\u589E\u52A0\u8BA1\u6570\u5668 <code>WeakReferenceCount</code> \u8BA1\u6570</li></ul></li><li>\u51CF\u5C11\u8BA1\u6570\uFF1A <ul><li>\u5728\u667A\u80FD\u6307\u9488\uFF08\u6216\u5F15\u7528\uFF09\u7684\u6790\u6784\u51FD\u6570\u4E2D\u51CF\u5C11\u8BA1\u6570\u5668 <code>SharedReferenceCount</code> \u8BA1\u6570</li><li>\u5728\u5F31\u6307\u9488\u7684\u6790\u6784\u51FD\u6570\u4E2D\u51CF\u5C11\u8BA1\u6570\u5668 <code>WeakReferenceCount</code> \u8BA1\u6570</li></ul></li><li>\u91CA\u653E\uFF1A\u5F53\u8BA1\u6570\u5668 <code>SharedReferenceCount</code> \u51CF\u5C11\u81F3 <code>0</code> \u65F6\uFF0C\u610F\u5473\u7740\u5F53\u524D\u5F15\u7528\u5BF9\u8C61\u5DF2\u7ECF\u6CA1\u6709\u6709\u6548\u7684\u667A\u80FD\u6307\u9488\uFF08\u6216\u5F15\u7528\uFF09\u5B58\u5728\u3002\u63A7\u5236\u5668\u5C06\u8C03\u7528 <code>DestroyObject()</code> \u65B9\u6CD5\u91CA\u653E\u5F15\u7528\u5BF9\u8C61\uFF0C\u540C\u65F6\u91CA\u653E\u6307\u5411\u8BE5\u5BF9\u8C61\u7684\u6240\u6709\u5F31\u6307\u9488\u4E0E\u63A7\u5236\u5668\u672C\u8EAB\u3002<code>DestroyObject()</code> \u65B9\u6CD5\u9ED8\u8BA4\u4F7F\u7528 <code>delete</code> \u64CD\u4F5C\u7B26\u91CA\u653E\u5BF9\u8C61\uFF0C\u4E5F\u53EF\u5728\u667A\u80FD\u6307\u9488\u6784\u9020\u65F6\u4F20\u5165\u81EA\u5B9A\u4E49\u7684 Deleter \u3002</li></ul><h2 id="\u5DE5\u5177\u51FD\u6570" tabindex="-1">\u5DE5\u5177\u51FD\u6570 <a class="header-anchor" href="#\u5DE5\u5177\u51FD\u6570" aria-hidden="true">#</a></h2><p><code>MakeShared&lt;&gt;()</code> \u6CE8\u518C\u667A\u80FD\u6307\u9488\uFF0C\u5C06\u63A7\u5236\u5668\u548C\u5F15\u7528\u5BF9\u8C61\u521D\u59CB\u5316\u5728\u8FDE\u7EED\u7684\u5185\u5B58\u4E0A</p><p><code>MakeSharable()</code> \u8F6C\u6362\u539F\u751F\u6307\u9488\u4E3A\u667A\u80FD\u6307\u9488</p><p><code>TSharedPtr::Reset()</code> \u5F3A\u5236\u91CA\u653E\u5F15\u7528\u5BF9\u8C61\u548C\u63A7\u5236\u5668</p><p><code>StaticCastSharedRef()</code> <code>ConstCastSharedRef()</code> \u667A\u80FD\u5F15\u7528\u7C7B\u578B\u8F6C\u6362</p><p><code>StaticCastSharedPtr()</code> <code>ConstCastSharedPtr()</code> \u667A\u80FD\u6307\u9488\u7C7B\u578B\u8F6C\u6362</p>',22),t=[d];function c(i,n,s,h,p,u){return a(),l("div",null,t)}var C=e(r,[["render",c]]);export{f as __pageData,C as default};
